mapper.select:
SELECT idx
FROM reservation
WHERE room_no = #{roomNum} AND check_in_datetime = #{date}

SELECT is_payment
FROM reservation_status
WHERE reserve_no = #{roomNum}

SELECT name
FROM room
WHERE idx = #{roomNum}

SELECT mid
FROM season
WHERE mid = #{date}

SELECT busiest
FROM season
WHERE busiest = #{date}

SELECT night
FROM room
WHERE name = #{room}

SELECT people_adult peopleAdult, people_child peopleChild, people_infant peopleInfant
FROM room
WHERE name = #{room}
